import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import navBar from "../navBar";

const EditLaboratory = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    title: "",
    country: "",
    state: "",
    city: "",
    pincode: "",
    address: "",
  });
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Error state for each field
  const [fieldErrors, setFieldErrors] = useState({
    title: "",
    country: "",
    state: "",
    city: "",
    pincode: "",
    address: "",
  });

  useEffect(() => {
    const fetchLaboratoryDetails = async () => {
      try {
        const response = await fetch(
          `http://localhost:8081/getLaboratoryById/${id}`
        );
        const data = await response.json();

        // Populate form fields with fetched data
        if (data) {
          setFormData({
            title: data.title,
            country: data.country,
            state: data.state,
            city: data.city,
            pincode: data.pincode,
            address: data.address,
          });
        }
      } catch (error) {
        console.error("Failed to fetch laboratory details:", error);
      }
    };

    fetchLaboratoryDetails();
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
    setFieldErrors({
      ...fieldErrors,
      [name]: "", // Clear the error when user starts typing
    });
  };

  const validateForm = () => {
    const { title, country, state, city, pincode, address } = formData;
    const errors = {};

    // Check for empty fields
    if (!title) errors.title = "Title is required!";
    if (!country) errors.country = "Country is required!";
    if (!state) errors.state = "State is required!";
    if (!city) errors.city = "City is required!";
    if (!pincode) errors.pincode = "Pincode is required!";
    if (!address) errors.address = "Address is required!";

    // Pincode validation (assuming it's a 6-digit number)
    if (pincode && (isNaN(pincode) || pincode.length !== 6)) {
      errors.pincode = "Pincode must be a 6-digit number.";
    }

    return errors;
  };

  const handleSave = async (e) => {
    e.preventDefault();

    setMessage("");
    setError("");

    // Validate the form and set errors
    const validationErrors = validateForm();
    if (Object.keys(validationErrors).length > 0) {
      setFieldErrors(validationErrors);
      return;
    }

    try {
      const response = await fetch(
        `http://localhost:8081/updateLaboratory/${id}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(formData),
        }
      );

      const result = await response.json();
      if (response.ok) {
        setMessage(result);
        setIsModalOpen(true);
      } else {
        setError(result);
      }
    } catch (error) {
      console.error("Error updating laboratory:", error);
      setError("An error occurred while updating the laboratory.");
    }
  };

  const closeModal = () => {
    setIsModalOpen(false);
    navigate("/laboratory");
  };

  return (
    <div>
      <div class="main-wrapper">
        <Navbar />

        <div class="page-wrapper">
          <div
            style={{
              padding: "30px",
              fontFamily: "'Roboto', sans-serif",
              backgroundColor: "#f9fafc",
              minHeight: "100vh",
            }}
          >
            <div
              style={{
                maxWidth: "600px",
                margin: "0 auto",
                background: "#fff",
                borderRadius: "10px",
                boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
                padding: "30px",
              }}
            >
              <h2
                style={{
                  marginBottom: "20px",
                  color: "#4e73df",
                  textAlign: "center",
                }}
              >
                Edit Laboratory
              </h2>

              {/* Form */}
              <form onSubmit={handleSave}>
                {/* Title */}
                <label style={{ fontWeight: "bold", marginBottom: "5px" }}>
                  Title:
                </label>
                <input
                  type="text"
                  name="title"
                  value={formData.title}
                  onChange={handleChange}
                  placeholder="Enter title"
                  style={{
                    width: "100%",
                    padding: "10px",
                    marginBottom: "15px",
                    fontSize: "1rem",
                    borderRadius: "5px",
                    border: "1px solid #ccc",
                  }}
                />
                {fieldErrors.title && (
                  <p style={{ color: "red", fontSize: "0.9rem" }}>
                    {fieldErrors.title}
                  </p>
                )}

                {/* Country */}
                <label style={{ fontWeight: "bold", marginBottom: "5px" }}>
                  Country:
                </label>
                <input
                  type="text"
                  name="country"
                  value={formData.country}
                  onChange={handleChange}
                  placeholder="Enter country"
                  style={{
                    width: "100%",
                    padding: "10px",
                    marginBottom: "15px",
                    fontSize: "1rem",
                    borderRadius: "5px",
                    border: "1px solid #ccc",
                  }}
                />
                {fieldErrors.country && (
                  <p style={{ color: "red", fontSize: "0.9rem" }}>
                    {fieldErrors.country}
                  </p>
                )}

                {/* State */}
                <label style={{ fontWeight: "bold", marginBottom: "5px" }}>
                  State:
                </label>
                <input
                  type="text"
                  name="state"
                  value={formData.state}
                  onChange={handleChange}
                  placeholder="Enter state"
                  style={{
                    width: "100%",
                    padding: "10px",
                    marginBottom: "15px",
                    fontSize: "1rem",
                    borderRadius: "5px",
                    border: "1px solid #ccc",
                  }}
                />
                {fieldErrors.state && (
                  <p style={{ color: "red", fontSize: "0.9rem" }}>
                    {fieldErrors.state}
                  </p>
                )}

                {/* City */}
                <label style={{ fontWeight: "bold", marginBottom: "5px" }}>
                  City:
                </label>
                <input
                  type="text"
                  name="city"
                  value={formData.city}
                  onChange={handleChange}
                  placeholder="Enter city"
                  style={{
                    width: "100%",
                    padding: "10px",
                    marginBottom: "15px",
                    fontSize: "1rem",
                    borderRadius: "5px",
                    border: "1px solid #ccc",
                  }}
                />
                {fieldErrors.city && (
                  <p style={{ color: "red", fontSize: "0.9rem" }}>
                    {fieldErrors.city}
                  </p>
                )}

                {/* Pincode */}
                <label style={{ fontWeight: "bold", marginBottom: "5px" }}>
                  Pincode:
                </label>
                <input
                  type="text"
                  name="pincode"
                  value={formData.pincode}
                  onChange={handleChange}
                  placeholder="Enter pincode"
                  style={{
                    width: "100%",
                    padding: "10px",
                    marginBottom: "15px",
                    fontSize: "1rem",
                    borderRadius: "5px",
                    border: "1px solid #ccc",
                  }}
                />
                {fieldErrors.pincode && (
                  <p style={{ color: "red", fontSize: "0.9rem" }}>
                    {fieldErrors.pincode}
                  </p>
                )}

                {/* Address */}
                <label style={{ fontWeight: "bold", marginBottom: "5px" }}>
                  Address:
                </label>
                <textarea
                  name="address"
                  value={formData.address}
                  onChange={handleChange}
                  placeholder="Enter address"
                  rows="4"
                  style={{
                    width: "100%",
                    padding: "10px",
                    fontSize: "1rem",
                    borderRadius: "5px",
                    border: "1px solid #ccc",
                    marginBottom: "20px",
                    resize: "none",
                  }}
                ></textarea>
                {fieldErrors.address && (
                  <p style={{ color: "red", fontSize: "0.9rem" }}>
                    {fieldErrors.address}
                  </p>
                )}

                {/* Error Message */}
                {error && (
                  <p style={{ color: "red", fontSize: "0.9rem" }}>{error}</p>
                )}

                {/* Save Button */}
                <button
                  type="submit"
                  style={{
                    width: "100%",
                    padding: "10px",
                    fontSize: "1rem",
                    backgroundColor: "#2E37A4",
                    color: "#fff",
                    border: "none",
                    borderRadius: "5px",
                    cursor: "pointer",
                  }}
                >
                  Save Changes
                </button>
              </form>

              {/* Success Modal */}
              {isModalOpen && (
                <div
                  style={{
                    position: "fixed",
                    top: 0,
                    left: 0,
                    width: "100%",
                    height: "100%",
                    backgroundColor: "rgba(0, 0, 0, 0.5)",
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    zIndex: 1000,
                  }}
                >
                  <div
                    style={{
                      backgroundColor: "#fff",
                      padding: "30px",
                      borderRadius: "10px",
                      textAlign: "center",
                      boxShadow: "0 4px 8px rgba(0,0,0,0.2)",
                    }}
                  >
                    <h3 style={{ marginBottom: "20px", color: "#4e73df" }}>
                      Laboratory Updated Successfully!
                    </h3>
                    <button
                      onClick={closeModal}
                      style={{
                        padding: "8px 16px",
                        fontSize: "1rem",
                        backgroundColor: "#4e73df",
                        color: "#fff",
                        border: "none",
                        borderRadius: "5px",
                        cursor: "pointer",
                      }}
                    >
                      Close
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EditLaboratory;
