import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import moment from "moment";

const EventDetailsPage = () => {
  const { date } = useParams();
  const [events, setEvents] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await axios.get(
          "http://localhost:8081/getallappointment",
          { withCredentials: true }
        );

        const eventsOnDate = response.data.filter((event) =>
          moment(event.time).isSame(date, "day")
        );

        setEvents(
          eventsOnDate.map((event) => ({
            id: event.id,
            title: event.name,
            time: moment(event.time).format("hh:mm A"),
            hour: moment(event.time).hour(),
            minute: moment(event.time).minute(),
          }))
        );
      } catch (error) {
        console.error("Error fetching events:", error);
      }
    };

    fetchEvents();
  }, [date]);

  return (
    <div style={{ padding: "20px", fontFamily: "'Poppins', sans-serif" }}>
      <h2 style={{ marginBottom: "20px" }}>
        Appointments on {moment(date).format("MMMM Do, YYYY")}
      </h2>

      <button
        onClick={() => navigate(-1)}
        style={{
          padding: "8px 16px",
          backgroundColor: "#4caf50",
          color: "#fff",
          border: "none",
          borderRadius: "4px",
          cursor: "pointer",
        }}
      >
        Go Back
      </button>

      <div
        style={{
          marginTop: "30px",
          display: "grid",
          gridTemplateColumns: "80px 1fr",
          gridGap: "10px",
          border: "1px solid #ddd",
          borderRadius: "8px",
          overflow: "hidden",
          boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
        }}
      >
        <div
          style={{ backgroundColor: "#f9f9f9", borderRight: "1px solid #ddd" }}
        >
          {Array.from({ length: 24 }).map((_, hour) => (
            <div
              key={hour}
              style={{
                height: "60px",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                borderBottom: "1px solid #eee",
              }}
            >
              <strong>{moment({ hour }).format("hh A")}</strong>
            </div>
          ))}
        </div>

        <div style={{ position: "relative", backgroundColor: "#fff" }}>
          {Array.from({ length: 24 }).map((_, hour) => (
            <div
              key={hour}
              style={{
                height: "60px",
                borderBottom: "1px solid #eee",
                position: "relative",
              }}
            >
              {events
                .filter((event) => event.hour === hour)
                .map((event) => (
                  <div
                    key={event.id}
                    style={{
                      position: "absolute",
                      top: `${(event.minute / 60) * 100}%`,
                      left: "5%",
                      right: "5%",
                      backgroundColor: "#4caf50",
                      color: "#fff",
                      borderRadius: "4px",
                      padding: "4px 8px",
                      fontSize: "12px",
                      boxShadow: "0 2px 5px rgba(0,0,0,0.2)",
                    }}
                  >
                    <strong>{event.title}</strong> - {event.time}
                  </div>
                ))}
            </div>
          ))}
        </div>
      </div>

      {events.length === 0 && (
        <p style={{ marginTop: "20px", textAlign: "center" }}>
          No events for this day.
        </p>
      )}
    </div>
  );
};

export default EventDetailsPage;
