import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";

const EditAppointment = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [appointment, setAppointment] = useState(null);
  const [time, setTime] = useState("");
  const [treatment, setTreatment] = useState("");
  const [message, setMessage] = useState("");

  // Error state for each field
  const [errors, setErrors] = useState({
    time: "",
    treatment: "",
    message: "",
  });

  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    const fetchAppointmentDetails = async () => {
      try {
        const response = await fetch(
          `http://localhost:8081/getAppointmentById/${id}`
        );
        const data = await response.json();
        setAppointment(data);

        // Parse time from database to valid datetime-local format
        const formattedTime = formatTimeToDatetimeLocal(data.time);
        setTime(formattedTime);

        setTreatment(data.treatment);
        setMessage(data.message || "");
      } catch (error) {
        console.error("Failed to fetch appointment:", error);
      }
    };

    fetchAppointmentDetails();
  }, [id]);

  // Helper function: Parse time to datetime-local format
  const formatTimeToDatetimeLocal = (dbTime) => {
    if (!dbTime) return "";

    const [datePart, timePart] = dbTime.split(" ");
    const [year, month, day] = datePart.split("-");
    const [hours, minutes, seconds] = timePart.split(":");

    // Ensure two-digit formatting
    const formattedHours = String(hours).padStart(2, "0");
    const formattedMinutes = String(minutes).padStart(2, "0");

    return `${year}-${month}-${day}T${formattedHours}:${formattedMinutes}`;
  };

  // Helper function: Convert time to 12-hour format with AM/PM
  const formatTimeTo12Hour = (time) => {
    if (!time) return "";
    const [date, timePart] = time.split("T");
    let [hours, minutes] = timePart.split(":");

    let period = "AM";
    hours = parseInt(hours, 10);

    if (hours >= 12) {
      period = "PM";
      if (hours > 12) hours -= 12;
    } else if (hours === 0) {
      hours = 12;
    }

    return `${hours}:${minutes} ${period}`;
  };

  const validateFields = () => {
    const newErrors = {
      time: time.trim() ? "" : "Time is required.",
      treatment: treatment.trim() ? "" : "Treatment is required.",
      message: message.trim() ? "" : "Message is required.",
    };
    setErrors(newErrors);
    return Object.values(newErrors).every((error) => error === "");
  };

  const handleSave = async () => {
    if (!validateFields()) {
      return; // Stop if validation fails
    }

    try {
      const response = await fetch(
        `http://localhost:8081/updateAppointment/${id}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            time,
            treatment,
            message,
          }),
        }
      );

      if (response.ok) {
        setIsModalOpen(true);
      } else {
        const errorMessage = await response.text();
        alert(`Failed to save changes: ${errorMessage}`);
      }
    } catch (error) {
      console.error("Error updating appointment:", error);
      alert("An error occurred while saving the changes.");
    }
  };

  const closeModal = () => {
    setIsModalOpen(false);
    navigate("/appointment");
  };

  return (
    <div
      style={{
        padding: "30px",
        fontFamily: "'Roboto', sans-serif",
        backgroundColor: "#f9fafc",
        minHeight: "100vh",
      }}
    >
      <div
        style={{
          maxWidth: "600px",
          margin: "0 auto",
          background: "#fff",
          borderRadius: "10px",
          boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
          padding: "30px",
        }}
      >
        <h2
          style={{
            marginBottom: "20px",
            color: "#4e73df",
            textAlign: "center",
          }}
        >
          Edit Appointment
        </h2>

        {appointment ? (
          <>
            {/* Appointment Details */}
            <div
              style={{
                marginBottom: "20px",
                borderBottom: "1px solid #eee",
                paddingBottom: "15px",
              }}
            >
              <p>
                <strong>Appointment No:</strong> {appointment.appointment_no}
              </p>
              <p>
                <strong>Name:</strong> {appointment.name}
              </p>
              <p>
                <strong>Contact:</strong> {appointment.mobileno}
              </p>
            </div>

            {/* Time Field */}
            <label style={{ fontWeight: "bold", marginBottom: "5px" }}>
              Time:
            </label>
            <input
              type="datetime-local"
              value={time}
              onChange={(e) => setTime(e.target.value)}
              style={{
                width: "100%",
                padding: "10px",
                marginBottom: "5px",
                fontSize: "1rem",
                borderRadius: "5px",
                border: "1px solid #ccc",
              }}
            />
            {errors.time && (
              <p style={{ color: "red", fontSize: "0.9rem" }}>{errors.time}</p>
            )}
            <p>
              <strong>Selected Time (12-Hour Format):</strong>{" "}
              {formatTimeTo12Hour(time)}
            </p>

            {/* Treatment Field */}
            <label style={{ fontWeight: "bold", marginBottom: "5px" }}>
              Treatment:
            </label>
            <input
              type="text"
              value={treatment}
              onChange={(e) => setTreatment(e.target.value)}
              placeholder="Enter treatment details"
              style={{
                width: "100%",
                padding: "10px",
                marginBottom: "5px",
                fontSize: "1rem",
                borderRadius: "5px",
                border: "1px solid #ccc",
              }}
            />
            {errors.treatment && (
              <p style={{ color: "red", fontSize: "0.9rem" }}>
                {errors.treatment}
              </p>
            )}

            {/* Save Button */}
            <button onClick={handleSave}>Save Changes</button>
          </>
        ) : (
          <p style={{ color: "red", textAlign: "center" }}>
            Loading appointment details...
          </p>
        )}
      </div>
    </div>
  );
};

export default EditAppointment;
