import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import moment from "moment";

const EventDetailsPage = () => {
  const { date } = useParams();
  const [events, setEvents] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await axios.get(
          "http://localhost:8081/getallappointment",
          { withCredentials: true }
        );

        console.log("Fetched Events on Date:", response.data);

        const eventsOnDate = response.data.filter((event) =>
          moment(event.time).isSame(date, "day")
        );

        setEvents(eventsOnDate);
      } catch (error) {
        console.error("Error fetching events:", error);
      }
    };

    fetchEvents();
  }, [date]);

  return (
    <div
      style={{
        padding: "20px",
        fontFamily: "'Poppins', sans-serif",
        maxWidth: "800px",
        margin: "0 auto",
      }}
    >
      <h2 style={{ marginBottom: "20px", textAlign: "center" }}>
        Appointments on {moment(date).format("MMMM Do, YYYY")}
      </h2>

      <button
        onClick={() => navigate(-1)}
        style={{
          padding: "8px 16px",
          backgroundColor: "#4caf50",
          color: "#fff",
          borderRadius: "4px",
          cursor: "pointer",
        }}
      >
        Go Back
      </button>

      {events.map((event) => (
        <div
          key={event.id}
          style={{
            padding: "10px",
            marginTop: "15px",
            border: "1px solid #ddd",
            borderRadius: "8px",
          }}
        >
          <strong>Name:</strong> {event.name} <br />
          <strong>Treatment:</strong> {event.treatment || "N/A"} <br />
          <strong>Mobile No:</strong> {event.mobileno || "N/A"} <br />
          <strong>Appointment No:</strong> {event.appointment_no || "N/A"}
        </div>
      ))}
    </div>
  );
};

export default EventDetailsPage;
