import React, { useState } from "react";
import { useLocation } from "react-router-dom";

const AppointmentReply = () => {
  const location = useLocation();
  const { appointment } = location.state || {};

  // State to store the reply message
  const [reply, setReply] = useState("");

  // Handle textarea change
  const handleReplyChange = (e) => {
    setReply(e.target.value);
  };

  // Handle reply submission
  const handleReplySubmit = async () => {
    if (!reply.trim()) {
      alert("Reply cannot be empty!");
      return;
    }

    try {
      const response = await fetch("https://your-api-endpoint/reply", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          appointment_no: appointment.appointment_no,
          name: appointment.name,
          date: appointment.time,
          reply: reply,
        }),
      });

      if (response.ok) {
        alert("Reply submitted successfully!");
        setReply("");
      } else {
        alert("Failed to submit reply.");
      }
    } catch (error) {
      console.error("Error:", error);
      alert("An error occurred while submitting the reply.");
    }
  };

  return (
    <div style={{ padding: "30px", fontFamily: "Arial, sans-serif" }}>
      <h2 style={{ marginBottom: "20px", color: "#4e73df" }}>
        Reply to Appointment
      </h2>

      {appointment ? (
        <div style={{ marginBottom: "30px" }}>
          <p>
            <strong>Appointment No:</strong> {appointment.appointment_no}
          </p>
          <p>
            <strong>Name:</strong> {appointment.name}
          </p>
          <p>
            <strong>Date:</strong>{" "}
            {new Date(appointment.time).toLocaleString("en-US", {
              year: "numeric",
              month: "2-digit",
              day: "2-digit",
              hour: "2-digit",
              minute: "2-digit",
              second: "2-digit",
              hour12: true,
              timeZone: "Asia/Kolkata",
            })}
          </p>
        </div>
      ) : (
        <p style={{ color: "red" }}>No appointment details available.</p>
      )}

      {/* Reply Textarea */}
      <div style={{ marginBottom: "20px" }}>
        <label
          htmlFor="reply"
          style={{ display: "block", marginBottom: "10px", fontWeight: "bold" }}
        >
          Your Reply:
        </label>
        <textarea
          id="reply"
          value={reply}
          onChange={handleReplyChange}
          placeholder="Type your reply here..."
          rows="5"
          style={{
            width: "100%",
            padding: "10px",
            fontSize: "1rem",
            border: "1px solid #ccc",
            borderRadius: "5px",
          }}
        ></textarea>
      </div>

      {/* Submit Button */}
      <button
        onClick={handleReplySubmit}
        style={{
          padding: "10px 20px",
          fontSize: "1rem",
          backgroundColor: "#4e73df",
          color: "#fff",
          border: "none",
          borderRadius: "5px",
          cursor: "pointer",
        }}
      >
        Submit Reply
      </button>
    </div>
  );
};

export default AppointmentReply;
