import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom"; // Import useNavigate for navigation
import Navbar from "../navBar";

const AddLaboratory = () => {
  const [formData, setFormData] = useState({
    title: "",
    country: "",
    state: "",
    city: "",
    pincode: "",
    address: "",
    name: "",
    mobileno: "",
    email: "",
    username: "",
    password: "",
  });

  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const [showModal, setShowModal] = useState(false); // Modal visibility state

  const navigate = useNavigate(); // Use navigate for navigation

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const validateForm = () => {
    const {
      title,
      country,
      state,
      city,
      pincode,
      address,
      name,
      mobileno,
      email,
      username,
      password,
    } = formData;

    if (
      !title ||
      !country ||
      !state ||
      !city ||
      !pincode ||
      !address ||
      !name ||
      !mobileno ||
      !email ||
      !username ||
      !password
    ) {
      return "All fields are required!";
    }

    if (isNaN(pincode) || pincode.length !== 6) {
      return "Pincode must be a 6-digit number.";
    }

    if (!/^[a-zA-Z0-9+_.-]+@[a-zA-Z0-9.-]+$/.test(email)) {
      return "Invalid email format.";
    }

    return null;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");
    setError("");

    const validationError = validateForm();
    if (validationError) {
      setError(validationError);
      return;
    }

    try {
      const response = await axios.post(
        "http://localhost:8081/addLaboratory",
        formData
      );
      setMessage(response.data);
      setShowModal(true);

      setFormData({
        title: "",
        country: "",
        state: "",
        city: "",
        pincode: "",
        address: "",
        name: "",
        mobileno: "",
        email: "",
        username: "",
        password: "",
      });

      setTimeout(() => {
        navigate("/laboratory");
      }, 4000);
    } catch (err) {
      setError(err.response?.data || "Failed to add laboratory");
    }
  };

  const handleCloseModal = () => {
    setShowModal(false);
    navigate("/laboratory");
  };

  return (
    <div className="main-wrapper">
      <Navbar />

      <div className="page-wrapper" style={{ marginTop: "50px" }}>
        <div style={{ flex: 1, padding: "20px", backgroundColor: "#fff" }}>
          <h2
            style={{
              fontSize: "28px",
              marginBottom: "20px",
              borderBottom: "1px solid #ddd",
              paddingBottom: "10px",
            }}
          >
            Add Laboratory
          </h2>

          <form
            onSubmit={handleSubmit}
            style={{
              display: "flex",
              flexWrap: "wrap",
              gap: "20px",
              maxWidth: "100%",
            }}
          >
            {[
              { name: "title", type: "text", placeholder: "Title" },
              { name: "country", type: "text", placeholder: "Country" },
              { name: "state", type: "text", placeholder: "State" },
              { name: "city", type: "text", placeholder: "City" },
              { name: "pincode", type: "number", placeholder: "Pincode" },
              { name: "address", type: "textarea", placeholder: "Address" },
              { name: "name", type: "text", placeholder: "Name" },
              { name: "mobileno", type: "text", placeholder: "Mobile No" },
              { name: "email", type: "email", placeholder: "Email" },
              { name: "username", type: "text", placeholder: "Username" },
              { name: "password", type: "password", placeholder: "Password" },
            ].map((field) => (
              <div key={field.name} style={{ flex: "1 1 calc(33.33% - 20px)" }}>
                {field.type === "textarea" ? (
                  <textarea
                    name={field.name}
                    placeholder={field.placeholder}
                    value={formData[field.name]}
                    onChange={handleChange}
                    required
                  ></textarea>
                ) : (
                  <input
                    type={field.type}
                    name={field.name}
                    placeholder={field.placeholder}
                    value={formData[field.name]}
                    onChange={handleChange}
                    required
                  />
                )}
              </div>
            ))}

            <button type="submit">Add Laboratory</button>
          </form>
          {message && <p style={{ color: "green" }}>{message}</p>}
          {error && <p style={{ color: "red" }}>{error}</p>}
        </div>
      </div>

      {showModal && (
        <div className="modal">
          <h3>Laboratory Added Successfully!</h3>
          <button onClick={handleCloseModal}>Close</button>
        </div>
      )}
    </div>
  );
};

export default AddLaboratory;
