import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";

const EditLaboratory = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    title: "",
    country: "",
    state: "",
    city: "",
    pincode: "",
    address: "",
    phone: "", // New Field
    email: "", // New Field
    contactPerson: "", // New Field
  });
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Error state for each field
  const [fieldErrors, setFieldErrors] = useState({
    title: "",
    country: "",
    state: "",
    city: "",
    pincode: "",
    address: "",
    phone: "",
    email: "",
    contactPerson: "",
  });

  useEffect(() => {
    const fetchLaboratoryDetails = async () => {
      try {
        const response = await fetch(
          `http://localhost:8081/getLaboratoryById/${id}`
        );
        const data = await response.json();

        if (data) {
          setFormData({
            title: data.title,
            country: data.country,
            state: data.state,
            city: data.city,
            pincode: data.pincode,
            address: data.address,
            phone: data.phone || "",
            email: data.email || "",
            contactPerson: data.contactPerson || "",
          });
        }
      } catch (error) {
        console.error("Failed to fetch laboratory details:", error);
      }
    };

    fetchLaboratoryDetails();
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
    setFieldErrors({
      ...fieldErrors,
      [name]: "",
    });
  };

  const validateForm = () => {
    const {
      title,
      country,
      state,
      city,
      pincode,
      address,
      phone,
      email,
      contactPerson,
    } = formData;
    const errors = {};

    if (!title) errors.title = "Title is required!";
    if (!country) errors.country = "Country is required!";
    if (!state) errors.state = "State is required!";
    if (!city) errors.city = "City is required!";
    if (!pincode) errors.pincode = "Pincode is required!";
    if (!address) errors.address = "Address is required!";
    if (!phone) errors.phone = "Phone number is required!";
    if (!email) errors.email = "Email is required!";
    if (!contactPerson) errors.contactPerson = "Contact person is required!";

    if (pincode && (isNaN(pincode) || pincode.length !== 6)) {
      errors.pincode = "Pincode must be a 6-digit number.";
    }

    if (phone && !/^\d{10}$/.test(phone)) {
      errors.phone = "Phone number must be 10 digits.";
    }

    if (email && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
      errors.email = "Invalid email format.";
    }

    return errors;
  };

  const handleSave = async (e) => {
    e.preventDefault();

    setMessage("");
    setError("");

    const validationErrors = validateForm();
    if (Object.keys(validationErrors).length > 0) {
      setFieldErrors(validationErrors);
      return;
    }

    try {
      const response = await fetch(
        `http://localhost:8081/updateLaboratory/${id}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(formData),
        }
      );

      const result = await response.json();
      if (response.ok) {
        setMessage(result.message || "Laboratory updated successfully!");
        setIsModalOpen(true);
      } else {
        setError(result.message || "Failed to update laboratory.");
      }
    } catch (error) {
      console.error("Error updating laboratory:", error);
      setError("An error occurred while updating the laboratory.");
    }
  };

  const closeModal = () => {
    setIsModalOpen(false);
    navigate("/laboratory");
  };

  return (
    <div
      style={{
        padding: "30px",
        fontFamily: "'Roboto', sans-serif",
        backgroundColor: "#f9fafc",
        minHeight: "100vh",
      }}
    >
      <div
        style={{
          maxWidth: "600px",
          margin: "0 auto",
          background: "#fff",
          borderRadius: "10px",
          boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
          padding: "30px",
        }}
      >
        <h2
          style={{
            marginBottom: "20px",
            color: "#4e73df",
            textAlign: "center",
          }}
        >
          Edit Laboratory
        </h2>

        <form onSubmit={handleSave}>
          {Object.keys(formData).map((field) => (
            <div key={field} style={{ marginBottom: "15px" }}>
              <label
                style={{
                  fontWeight: "bold",
                  marginBottom: "5px",
                  textTransform: "capitalize",
                }}
              >
                {field}:
              </label>
              <input
                type="text"
                name={field}
                value={formData[field]}
                onChange={handleChange}
                placeholder={`Enter ${field}`}
                style={{
                  width: "100%",
                  padding: "10px",
                  fontSize: "1rem",
                  borderRadius: "5px",
                  border: "1px solid #ccc",
                }}
              />
              {fieldErrors[field] && (
                <p style={{ color: "red", fontSize: "0.9rem" }}>
                  {fieldErrors[field]}
                </p>
              )}
            </div>
          ))}

          {error && <p style={{ color: "red", fontSize: "0.9rem" }}>{error}</p>}

          <button
            type="submit"
            style={{
              width: "100%",
              padding: "10px",
              fontSize: "1rem",
              backgroundColor: "#2E37A4",
              color: "#fff",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
            }}
          >
            Save Changes
          </button>
        </form>

        {isModalOpen && (
          <div
            style={{
              position: "fixed",
              top: 0,
              left: 0,
              width: "100%",
              height: "100%",
              backgroundColor: "rgba(0, 0, 0, 0.5)",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              zIndex: 1000,
            }}
          >
            <div
              style={{
                backgroundColor: "#fff",
                padding: "30px",
                borderRadius: "10px",
                textAlign: "center",
                boxShadow: "0 4px 8px rgba(0,0,0,0.2)",
              }}
            >
              <h3 style={{ marginBottom: "20px", color: "#4e73df" }}>
                Laboratory Updated Successfully!
              </h3>
              <button
                onClick={closeModal}
                style={{
                  padding: "8px 16px",
                  fontSize: "1rem",
                  backgroundColor: "#4e73df",
                  color: "#fff",
                  border: "none",
                  borderRadius: "5px",
                  cursor: "pointer",
                }}
              >
                Close
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default EditLaboratory;
