import React, { useState, useEffect } from "react";
import { Calendar, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import axios from "axios";
import "react-big-calendar/lib/css/react-big-calendar.css";
import { useNavigate } from "react-router-dom";

const localizer = momentLocalizer(moment);

const FullPageCalendar = () => {
  const [events, setEvents] = useState([]);
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedMonth, setSelectedMonth] = useState(new Date().getMonth());
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());
  const navigate = useNavigate();

  // Fetch events from the backend
  const fetchEvents = async () => {
    try {
      const response = await axios.get(
        "http://localhost:8081/getallappointment",
        { withCredentials: true }
      );

      const formattedEvents = response.data.map((event) => {
        const startTime = moment(event.time).toDate();
        const endTime = moment(event.time).add(1, "hours").toDate();

        return {
          id: event.id,
          title: event.name,
          start: startTime,
          end: endTime,
          time: moment(startTime).format("hh:mm A"),
        };
      });

      setEvents(formattedEvents);
    } catch (error) {
      console.error("Error fetching events:", error);
    }
  };

  useEffect(() => {
    fetchEvents();
  }, [selectedMonth, selectedYear]);

  const handleMonthChange = (e) => {
    const newMonth = parseInt(e.target.value);
    setSelectedMonth(newMonth);
    setCurrentDate(new Date(selectedYear, newMonth, 1));
  };

  const handleYearChange = (e) => {
    const newYear = parseInt(e.target.value);
    setSelectedYear(newYear);
    setCurrentDate(new Date(newYear, selectedMonth, 1));
  };

  const handleEventClick = (event) => {
    const date = moment(event.start).format("YYYY-MM-DD");
    navigate(`/events/${date}`);
  };

  const handleSelectSlot = (slotInfo) => {
    const date = moment(slotInfo.start).format("YYYY-MM-DD");
    navigate(`/events/${date}`);
  };

  const eventPropGetter = (event) => ({
    style: {
      backgroundColor: "#4caf50",
      borderRadius: "8px",
      color: "white",
      border: "none",
      textAlign: "center",
      fontWeight: "500",
      fontSize: "12px",
      padding: "2px 4px",
    },
  });

  const eventStack = (slotEvents) => {
    // If more than 2 events exist at the same time, group them
    const eventCount = slotEvents.length;
    if (eventCount > 2) {
      return (
        <div>
          {slotEvents.slice(0, 2).map((event) => (
            <div
              key={event.id}
              onClick={() => handleEventClick(event)}
              style={eventPropGetter(event).style}
            >
              {event.title}
            </div>
          ))}
          <div
            onClick={() =>
              navigate(
                `/events/${moment(slotEvents[0].start).format("YYYY-MM-DD")}`
              )
            }
            style={{
              ...eventPropGetter(slotEvents[0]).style,
              cursor: "pointer",
              marginTop: "5px",
            }}
          >
            +{eventCount - 2} more
          </div>
        </div>
      );
    }

    return slotEvents.map((event) => (
      <div
        key={event.id}
        onClick={() => handleEventClick(event)}
        style={eventPropGetter(event).style}
      >
        {event.title}
      </div>
    ));
  };

  return (
    <div
      style={{
        height: "100vh",
        padding: "20px",
        fontFamily: "'Poppins', sans-serif",
        color: "#333",
      }}
    >
      {/* Header Controls */}
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          marginBottom: "20px",
          padding: "10px 20px",
          backgroundColor: "#fff",
          borderRadius: "8px",
          boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
        }}
      >
        <h2 style={{ margin: 0, color: "#4caf50" }}>📅 My Calendar</h2>
        <div style={{ display: "flex", gap: "15px" }}>
          <label>
            <strong>Month:</strong>{" "}
            <select
              value={selectedMonth}
              onChange={handleMonthChange}
              style={{
                padding: "6px 12px",
                borderRadius: "4px",
                border: "1px solid #ddd",
                cursor: "pointer",
              }}
            >
              {moment.months().map((month, index) => (
                <option key={index} value={index}>
                  {month}
                </option>
              ))}
            </select>
          </label>

          <label>
            <strong>Year:</strong>{" "}
            <select
              value={selectedYear}
              onChange={handleYearChange}
              style={{
                padding: "6px 12px",
                borderRadius: "4px",
                border: "1px solid #ddd",
                cursor: "pointer",
              }}
            >
              {Array.from(
                { length: 10 },
                (_, i) => new Date().getFullYear() - 5 + i
              ).map((year) => (
                <option key={year} value={year}>
                  {year}
                </option>
              ))}
            </select>
          </label>
        </div>
      </div>

      {/* Calendar Component */}
      <div
        style={{
          marginTop: "10px",
          borderRadius: "12px",
          overflow: "hidden",
          boxShadow: "0 4px 15px rgba(0, 0, 0, 0.15)",
        }}
      >
        <Calendar
          localizer={localizer}
          events={events}
          startAccessor="start"
          endAccessor="end"
          date={currentDate}
          onNavigate={(date) => setCurrentDate(date)}
          views={["month", "week", "day"]}
          style={{ height: "80vh", backgroundColor: "#fff" }}
          eventPropGetter={eventPropGetter}
          onSelectEvent={handleEventClick}
          onSelectSlot={handleSelectSlot}
          selectable
          components={{
            event: ({ event }) => {
              // Custom render for events
              return <div>{event.title}</div>;
            },
          }}
          popup={true}
          onSelectEvent={(event) => handleEventClick(event)}
          onSelectSlot={handleSelectSlot}
          eventStack={eventStack} // Our custom event stacking for more than 2 events
        />
      </div>
    </div>
  );
};

export default FullPageCalendar;
