import React, { useState } from "react";
import axios from "axios";
import Navbar from "../navBar";

const AddLaboratory = () => {
  const [formData, setFormData] = useState({
    title: "",
    country: "",
    state: "",
    city: "",
    pincode: "",
    address: "",
  });

  const [message, setMessage] = useState("");
  const [error, setError] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");
    setError("");

    try {
      const response = await axios.post("/addLaboratory", formData);
      setMessage(response.data);
      setFormData({
        title: "",
        country: "",
        state: "",
        city: "",
        pincode: "",
        address: "",
      });
    } catch (err) {
      setError(err.response?.data || "Failed to add laboratory");
    }
  };

  return (
    <div style={{ display: "flex" }}>
      {/* Sidebar and Navbar Integration */}
      <Navbar />
      <div
        style={{
          flex: 1,
          padding: "20px",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          backgroundColor: "#f9fafc",
          minHeight: "100vh",
        }}
      >
        {/* Form Container */}
        <div
          style={{
            width: "450px",
            padding: "30px",
            backgroundColor: "#fff",
            borderRadius: "10px",
            boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
            fontFamily: "Arial, sans-serif",
          }}
        >
          <h2
            style={{
              textAlign: "center",
              marginBottom: "20px",
              fontSize: "24px",
              color: "#333",
            }}
          >
            Add Laboratory
          </h2>
          {message && (
            <p style={{ color: "green", textAlign: "center" }}>{message}</p>
          )}
          {error && (
            <p style={{ color: "red", textAlign: "center" }}>{error}</p>
          )}
          <form
            onSubmit={handleSubmit}
            style={{ display: "flex", flexDirection: "column", gap: "15px" }}
          >
            {/* Input Fields */}
            {["title", "country", "state", "city", "pincode", "address"].map(
              (field, index) => (
                <input
                  key={index}
                  type={field === "pincode" ? "number" : "text"}
                  name={field}
                  placeholder={field.charAt(0).toUpperCase() + field.slice(1)}
                  value={formData[field]}
                  onChange={handleChange}
                  required
                  style={{
                    padding: "12px",
                    border: "1px solid #ccc",
                    borderRadius: "6px",
                    fontSize: "14px",
                  }}
                />
              )
            )}

            {/* Address Field */}
            <textarea
              name="address"
              placeholder="Address"
              value={formData.address}
              onChange={handleChange}
              required
              style={{
                padding: "12px",
                border: "1px solid #ccc",
                borderRadius: "6px",
                resize: "none",
                fontSize: "14px",
                height: "80px",
              }}
            ></textarea>

            {/* Submit Button */}
            <button
              type="submit"
              style={{
                padding: "12px",
                backgroundColor: "#4CAF50",
                color: "white",
                border: "none",
                borderRadius: "6px",
                cursor: "pointer",
                fontSize: "16px",
              }}
            >
              Add Laboratory
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default AddLaboratory;
